"use strict";function loadPokedex(){let e=$("<div>").text("Loading...");$("#loading-container").append(e),fetch("https://pokeapi.co/api/v2/pokemon/?limit=151").then(e=>e.json()).then(t=>{e.remove();let o=$("#pokedex-list");for(let a=0;a<t.results.length;a++){let n=t.results[a].name,r=t.results[a].url,s=r.split("/").slice(-2,-1)[0],i=`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${s}.png`;fetch(r).then(e=>e.json()).then(e=>{let t=e.types[0].type.name,a=e.height,r=$("<li>").addClass("list-group-item"),s=$("<a>").addClass("btn btn-primary").attr("href","#").text(n),l=$("<img>").attr("src",i).attr("alt",n).addClass("ml-2");s.append(l),r.append(s),r.css("background-color",getBackgroundColorForType(t)),o.append(r),s.click(()=>{$("#pokemon-name").text(n),$("#pokemon-height").text(a),$("#pokemon-image").attr("src",i),$("#pokemon-modal").modal("show")})})}}).catch(t=>{e.remove();let o=$("<div>").text("Failed to load Pokedex.");$("#loading-container").append(o)})}function getBackgroundColorForType(e){return({normal:"#A8A878",fire:"#F08030",water:"#6890F0",electric:"#F8D030",grass:"#78C850",ice:"#98D8D8",fighting:"#C03028",poison:"#A040A0",ground:"#E0C068",flying:"#A890F0",psychic:"#F85888",bug:"#A8B820",rock:"#B8A038",ghost:"#705898",dragon:"#7038F8",dark:"#705848",steel:"#B8B8D0",fairy:"#EE99AC"})[e]||"#F3F3F3"}function searchPokemon(){let e=$("#search-bar"),t=e.val().toLowerCase().trim();t?$("#pokedex-list > li").each(function(){let e=$(this).find("a").text().toLowerCase();e.includes(t)?$(this).show():$(this).hide()}):$("#pokedex-list > li").show()}$(document).ready(()=>{loadPokedex(),$("#search-button").click(searchPokemon),$("#search-bar").on("input",searchPokemon)});